import React from 'react';
import { useParams } from 'react-router-dom'

const ExploreDetails = ({data}) => {

    const {name } = useParams()
    return (
        <>
        {
            data
            .filter((dish) => dish.name === name )
            .map(dish => (
                <div key={dish.name}>
                    {/* <img>{dish.img}</img> */}
                    <ProdcutsTitle>{dish.desc}</ProdcutsTitle>
                    <h2>{dish.name}</h2>
                    <h2>{dish.button}</h2>
                </div>
            ))
        }
            
        </>
    )
}

export default ExploreDetails
